import React, { useRef } from "react";
import { GraphModel } from "../models/GraphModel";

interface ToolbarProps {
  graph: GraphModel;
  onGraphChange: (newGraph: GraphModel) => void;
}

export const GraphToolbar = ({ graph, onGraphChange }: ToolbarProps) => {
  const fileInputRef = useRef<HTMLInputElement>(null);

  // --- SAVE (EXPORT) ---
  const handleSave = () => {
    const jsonString = graph.exportJSON();
    const blob = new Blob([jsonString], { type: "application/json" });
    const url = URL.createObjectURL(blob);

    // Create a temporary link to trigger the download
    const link = document.createElement("a");
    link.href = url;
    link.download = `graph-${new Date().toISOString().slice(0, 10)}.json`; // e.g. graph-2023-10-27.json
    document.body.appendChild(link);
    link.click();

    // Cleanup
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  // --- LOAD (IMPORT) ---
  const handleLoadClick = () => {
    // Trigger the hidden file input
    fileInputRef.current?.click();
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();

    reader.onload = (event) => {
      const content = event.target?.result;
      if (typeof content === "string") {
        try {
          // 1. Parse JSON into GraphModel
          const newGraph = GraphModel.fromJSON(content);

          // 2. Update State
          onGraphChange(newGraph);

          // 3. Reset input so selecting the same file again works
          if (fileInputRef.current) fileInputRef.current.value = "";
        } catch (err) {
          alert("Failed to load graph. Invalid JSON file.");
        }
      }
    };

    reader.readAsText(file);
  };

  return (
    <div style={{ padding: 10, borderBottom: "1px solid #ccc", background: "#f5f5f5", display: "flex", gap: "10px" }}>
      <button onClick={handleSave}>ðŸ’¾ Save JSON</button>

      <button onClick={handleLoadClick}>ðŸ“‚ Load JSON</button>

      {/* Hidden Input for File Upload */}
      <input
        type="file"
        ref={fileInputRef}
        style={{ display: "none" }}
        accept=".json"
        onChange={handleFileChange}
      />
    </div>
  );
};
