import React from "react";
// Adjust this import path if needed
import { ContextMenu } from "./ContextMenu"; 

// 1. Relaxed Interface to match your Parent Component
export interface CanvasMenuState {
  targetId?: string | null; // Allows string, null, or undefined
  position: { x: number; y: number };
}

interface GraphContextMenuProps {
  canvasMenu: CanvasMenuState | null;
  graph: any; 
  onGraphChange: (newGraph: any) => void;
  onClose: () => void;
}

export const GraphContextMenu: React.FC<GraphContextMenuProps> = ({
  canvasMenu,
  graph,
  onGraphChange,
  onClose,
}) => {
  if (!canvasMenu) return null;

  const getOptions = () => {
    // 1. CANVAS (Background)
    if (!canvasMenu.targetId) {
      return [
        {
          label: "Add Node",
          onClick: () => {
            const newGraph = graph.clone();
            const id = `n_${crypto.randomUUID().slice(0, 4)}`;
            newGraph.addNode({
              id,
              label: "New Node",
              position: canvasMenu.position,
              ports: [],
              size: { width: 100, height: 50 },
            });
            onGraphChange(newGraph);
            onClose();
          },
        },
        {
          label: "Add Container",
          onClick: () => {
            const newGraph = graph.clone();
            const id = `c_${crypto.randomUUID().slice(0, 4)}`;
            newGraph.addContainer({
              id,
              label: "New Container",
              position: canvasMenu.position,
              size: { width: 300, height: 200 },
              nodeIds: [],
              childContainerIds: [],
              collapsed: false,
            });
            onGraphChange(newGraph);
            onClose();
          },
        },
      ];
    }

    return [];
  };

  return (
    <>
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          zIndex: 9998,
        }}
        onMouseDown={(e) => {
           e.stopPropagation();
           onClose();
        }}
        onContextMenu={(e) => {
          e.preventDefault();
          e.stopPropagation();
          onClose();
        }}
      />

      <div
        style={{
          position: "absolute",
          top: canvasMenu.position.y,
          left: canvasMenu.position.x,
          zIndex: 9999,
        }}
      >
        <ContextMenu position={canvasMenu.position} options={getOptions()} />
      </div>
    </>
  );
};
